document.addEventListener('DOMContentLoaded', function() { const dfwButton = document.getElementById('generate-dfw-export'); const houstonButton = document.getElementById('generate-houston-export'); const westTexasButton = document.getElementById('generate-west-texas-export'); if (dfwButton) { dfwButton.addEventListener('click', function() { generateRegionalExport('dfw'); }); } if (houstonButton) { houstonButton.addEventListener('click', function() { generateRegionalExport('houston'); }); } if (westTexasButton) { westTexasButton.addEventListener('click', function() { generateRegionalExport('west_texas'); }); } function generateRegionalExport(region) { const button = document.getElementById(`generate-${region}-export`); const originalText = button.innerHTML; button.disabled = true; button.innerHTML = '<span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span> Processing...'; fetch(`/api/generate-regional-billing/${region}`, { method: 'POST', headers: { 'Content-Type': 'application/json', 'X-Requested-With': 'XMLHttpRequest' } }) .then(response => response.json()) .then(data => { button.disabled = false; button.innerHTML = originalText; const alertsContainer = document.getElementById('alerts-container'); if (data.success) { const alert = document.createElement('div'); alert.className = 'alert alert-success alert-dismissible fade show'; alert.innerHTML = ` <strong>Success!</strong> ${data.message} <a href="${data.download_url}" class="btn btn-sm btn-primary ms-3">Download Export</a> <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button> `; alertsContainer.appendChild(alert); new bootstrap.Alert(alert); } else { const alert = document.createElement('div'); alert.className = 'alert alert-danger alert-dismissible fade show'; alert.innerHTML = ` <strong>Error!</strong> ${data.message} <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button> `; alertsContainer.appendChild(alert); new bootstrap.Alert(alert); } }) .catch(error => { button.disabled = false; button.innerHTML = originalText; const alertsContainer = document.getElementById('alerts-container'); const alert = document.createElement('div'); alert.className = 'alert alert-danger alert-dismissible fade show'; alert.innerHTML = ` <strong>Error!</strong> An unexpected error occurred while generating the export. <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button> `; alertsContainer.appendChild(alert); new bootstrap.Alert(alert); console.error('Export error:', error); }); } });